package dataTypeExample;

public class floatDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float my_marks = (float) 60.05;
		System.out.println("My mark is:"+my_marks);
		
		float my_new_marks = 80.05f;
		System.out.println("My mark is:"+my_new_marks);
		
		double my_brand_new_marks = 100.06;
		System.out.println("My mark is:"+my_brand_new_marks);
		
		//Typecasting (Downcasting)
		int a = (int) 45.46;
		System.out.println(a);
	}

}
