package dataTypeExample;

public class intDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte marks = 98;
		System.out.println("My marks:"+marks);
		
		short total_money = 150;
		System.out.println("My marks:"+total_money);
		
		long number = 1500000;
		System.out.println("My marks:"+number);
		
		int mobileNumber = 150000000;
		System.out.println("My marks:"+mobileNumber);
	}
	
}
